mapper.select:
select tb_bilibili_danmu.* from tb_bilibili_danmu
where send_time >= #{startTime}
and send_time <= #{endTime}

    and roomid = #{roomid}

ORDER BY id DESC limit #{page}, #{pageSize}

select tb_bilibili_danmu.* from tb_bilibili_danmu
join (select tb_bilibili_danmu.id from tb_bilibili_danmu
where send_time >= #{startTime}
and send_time <= #{endTime}

    and roomid = #{roomid}

ORDER BY id DESC limit #{page}, #{pageSize}) tid using (id)

select count(*) from tb_bilibili_danmu
where send_time >= #{startTime} and send_time <= #{endTime}

    and roomid = #{roomid}
